import { TextField } from "@mui/material";
import React, { useEffect, useRef } from "react";
import { Controller, useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import CommonButton from "../common/button/CommonButton";

function UseRefHook() {
  const schema = yup
    .object({
      firstName: yup.string().required(),
      age: yup.number().positive().integer().required(),
    })
    .required();

  const useRef1 = useRef("");
  const useRef2 = useRef("");
  const {
    register,
    handleSubmit,
    watch,
    control,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  useEffect(() => {
    useRef1.current.focus();
  }, []);

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      useRef2.current.focus();
    }
  };
  console.log("focus value", useRef2);
  const onSubmit = (data) => console.log(data);
  return (
    <div className="pt-16 flex justify-center space-x-2">
      <form onSubmit={handleSubmit(onSubmit)}>
        <Controller
          name="firstName"
          control={control}
          render={({ field }) => (
            <TextField
              inputRef={useRef1}
              type="text"
              {...field}
              name="firstName"
              label="First Name"
              className="border pl-2"
              onKeyDown={(e) => {
                handleKeyPress(e);
              }}
            />
          )}
        />
        <TextField
          type="text"
          inputRef={useRef2}
          name="lastName"
          label="Last Name"
          className="border pl-2"
        />
        <CommonButton type="submit" label="Save" className="bg" />
      </form>
    </div>
  );
}

export default UseRefHook;
